mapper.select:
SELECT email, password, nickname, exp, title, created_at, is_admin, mode, is_deleted, profile_image_url
FROM Members
WHERE email = #{email}

mapper.update:
UPDATE Members
SET password = #{changedPassword}
WHERE email = #{member.email}

UPDATE Members
SET nickname = #{nickname},
title = #{title},
mode = #{mode}
WHERE email = #{email}

UPDATE Members
SET is_deleted = true
WHERE email = #{email}

mapper.select:
SELECT *
FROM members m
WHERE m.email LIKE CONCAT('%', #{email}, '%')
AND m.email NOT IN (
SELECT f.target_email
FROM follow_requests f
WHERE f.requester_email = #{requesterEmail}
AND f.status = 'APPLIED'
)
AND m.email != #{requesterEmail}
LIMIT #{limit} OFFSET #{offset}
